import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [data, setData] = useState(null); //init useState
    const fetchData = async () => { //async function to get the data
      try { //fetch the api data from the /api/ endpoint
        const response = await fetch('/api'); // Assuming "John" is the name you want to send
        if (!response.ok) { //error checking :)
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error('Error is:', error);
      }
    };
  return (
    <div id='page'>
      {/* Below this will be the search bar, where the user will input the country */}
    <section className="searchBar">
        <h3 id='greetings'>Greetings! Search your country!</h3>
        <input id='search' type='text' placeholder="Type your string"/>
    </section>
    {/* After this button is pressed, the country will be processed, then the information will be displayed */}
    <button id='btn' onClick={fetchData}>Search</button>
    {/* Below here, in the article section, info about the country will be placed */}
    <article id='info-page'>
      <section id='info-header'> 
        <h1>Country Name</h1>
        <img alt='flag of country'/>
      </section>
    </article>
    </div>
  );
}

export default App;
